const initialState = {
  userRegisteredNumber: {}, // Change this to an empty object
  OTP: {}, // Change this to an empty object
};

const registrationReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_REGISTERED_NUMBER":
      // Update state.userRegisteredNumber with the new payload
      const newUserRegisteredNumber = {
        ...state.userRegisteredNumber,
      };

      // Update local storage with the new state
      localStorage.setItem(
        "userRegisteredNumber",
        JSON.stringify(newUserRegisteredNumber)
      );

      return {
        ...state,
        userRegisteredNumber: newUserRegisteredNumber,
      };

    case "ADD_OTP":
      // Update state.OTP with the new payload
      const newOtpReceived = {
        ...state.OTP,
        [action.payload.id]: action.payload.data,
      };

      // Update local storage with the new state
      localStorage.setItem("otp", JSON.stringify(newOtpReceived));

      return {
        ...state,
        OTP: newOtpReceived,
      };

    default:
      return state;
  }
};

export default registrationReducer;
