import React, { useState } from "react";
import OtpInput from "react-otp-input";

import image from "../../assets/undraw_confirmed_81ex.jpg";

import "./styles.scss";

const Otp = () => {
  const phoneNumber = +918465035940;
  const onResend = () => {};

  const [otp, setOtp] = useState("");
  //console.log(otp);

  return (
    <div className="otp-container">
      <img src={`${image}`} alt="logo" className="logo" />
      <h1>Please verify Mobile number</h1>
      <div className="number-edit-container">
        <p>
          Please sign in to your account<span>{phoneNumber}</span>
        </p>
        <p>Change Phone Number</p>
      </div>
      <div className="otp-input-container">
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={4}
          separator={<span style={{ width: "8px" }}></span>}
          renderInput={(props) => <input {...props} />}
          containerStyle="custom-otp-input-container"
        />
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={6}
          separator={<span style={{ width: "8px" }}></span>}
          isInputNum={true}
          shouldAutoFocus={true}
          inputStyle={{
            border: "1px solid transparent",
            borderRadius: "8px",
            width: "54px",
            height: "54px",
            fontSize: "12px",
            color: "#000",
            fontWeight: "400",
            caretColor: "blue",
          }}
          focusStyle={{
            border: "1px solid #CFD3DB",
            outline: "none",
          }}
        />
      </div>
      <div>
        <p>
          Didnâ€™t receive the code?<span onClick={onResend}>Resend</span>
        </p>
        <button className="submit-button">Verify</button>
      </div>
    </div>
  );
};

export default Otp;
