import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import OtpInput from "react-otp-input";
import Snackbar from "../snackbar/snackbar";

import image from "../../assets/undraw_confirmed_81ex.jpg";

import "./styles.scss";

const SnackbarType = {
  success: "success",
  fail: "fail",
};

const Otp = () => {
  const navigate = useNavigate();
  const mobileNumber = localStorage.getItem("userRegisterdNumber");
  const enteredOTP = 3223;
  const snackbarRef = useRef(null);

  const [otp, setOtp] = useState("");
  const [validation, setValidation] = useState("");

  const onResend = () => {};

  useEffect(() => {
    snackbarRef.current.show();
  });

  const onChangeNumber = () => {
    navigate("/");
  };
  const validateOTP = () => {
    const apiUrl = "http://localhost:3001/validate-otp";
    // Data to send in the POST request
    const data = {
      mobileNumber: "8465035940", // Replace with the mobile number
      enteredOTP: "123456", // Replace with the OTP entered by the user
    };
    axios
      .post("http://localhost:3001/validate-otp", { mobileNumber, enteredOTP })
      .then((response) => {
        setValidation(response.data.message);
        setOtp(response.data.message);
      })
      .catch((error) => {
        console.error("Error:", error.message);
        setValidation("OTP verification failed.");
      });
  };

  return (
    <div className="otp-container">
      <img src={`${image}`} alt="logo" className="logo" />
      <h1>Please verify Mobile number</h1>
      <div className="number-edit-container">
        <p>
          Please sign in to your account<span>{mobileNumber}</span>
        </p>
        <p onClick={onChangeNumber}>Change Phone Number</p>
      </div>
      <div className="otp-input-container">
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={4}
          renderSeparator={<span style={{ width: "8px" }}></span>}
          renderInput={(props) => <input {...props} />}
          inputStyle={{
            border: "solid #e5e5e5 1px",
            borderRadius: "8px",
            width: "48px",
            height: "48px",
            fontSize: "18px",
            color: "#333",
            fontWeight: "500",
            caretColor: "blue",
            fontStyle: "normal",
            fontFamily: "Work Sans",
            boxShadow: "6px 6px 5px 0px rgba(224,222,222,0.75)",
            outlineColor: "#80848c",
          }}
          focusStyle={{
            border: "1px solid #637290",
            outline: "none",
          }}
        />
        {validation && <p>{validation}</p>}
      </div>
      <div>
        <p>
          Didn’t receive the code?<span onClick={onResend}>Resend</span>
        </p>
        <button className="submit-button" onClick={validateOTP}>
          Verify
        </button>
      </div>
      <Snackbar
        ref={snackbarRef}
        message={`Your OTP is ${enteredOTP}`}
        type={SnackbarType.success}
      />
    </div>
  );
};

export default Otp;
