import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";

import VerificationContext from "../verifcationContext/verificationContext";

import "./signin.scss";
import logo from "../../assets/AK_logo.jpg";

const Signin = () => {
  const navigate = useNavigate();
  const [phoneNumber, setPhoneNumber] = useState("");
  const [phoneError, setPhoneError] = useState("");

  const isPhoneNumberValid = (phoneNumber) => {
    console.log(phoneNumber);
    const phoneRegex = /^[0-9]{12}$/;
    return phoneRegex.test(phoneNumber);
  };

  const handlePhoneChange = (value) => {
    setPhoneNumber(value);
    setPhoneError("");
  };

  const handleSubmit = () => {
    if (!isPhoneNumberValid(phoneNumber)) {
      setPhoneError("Invalid phone number");
      return;
    }
    setPhoneError("");
    navigate("/otp");
  };

  return (
    <VerificationContext.Consumer>
      {(value) => {
        const { setActivePhoneNumber } = value;
        setActivePhoneNumber(phoneNumber);

        return (
          <div className="signin-container">
            <img src={`${logo}`} alt="logo" className="logo" />
            <div className="welcome-text">
              <h1>Welcome Back</h1>
              <p>Please sign in to your account</p>
            </div>
            <div>
              <div className="number-input-container">
                <PhoneInput
                  inputProps={{
                    name: "contact",
                    required: true,
                    autoFocus: true,
                  }}
                  className="input"
                  country={"in"}
                  value={phoneNumber}
                  onChange={(value) => handlePhoneChange(value)}
                  inputStyle={{
                    border: "none",
                    borderRadius: "8px",
                    width: "330px",
                    height: "32px",
                    fontSize: "18px",
                    color: "#333",
                    fontWeight: "500",
                    caretColor: "blue",
                    fontStyle: "normal",
                    fontFamily: "Work Sans",
                  }}
                  containerStyle={{
                    border: "none",
                    backgroundColor: "none",
                  }}
                />
              </div>
              {phoneError ? (
                <p className="error-message">{phoneError}</p>
              ) : (
                <p>
                  We will send you a one time SMS message. Charges may apply.
                </p>
              )}
            </div>
            <button className="submit-button" onClick={handleSubmit}>
              Sign In with OTP
            </button>
          </div>
        );
      }}
    </VerificationContext.Consumer>
  );
};

export default Signin;
