const initialState = {
  userRegisteredNumber: {
    
  },
  OTP: {},
};

const registrationReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_REGISTERED_NUMBER":
      // Update state
      const newRegisteredNumber = {
        ...state,
        userRegisteredNumber: {
          ...state.userRegisteredNumber,
          ...action.payload,
        },
      };

      // Update local storage with the new state
      localStorage.setItem(
        "userRegisteredNumber",
        JSON.stringify(newRegisteredNumber.userRegisteredNumber)
      );

      return newRegisteredNumber;

    case "ADD_OTP":
      // Update state
      const newOtpReceived = {
        ...state,
        OTP: {
          ...state.OTP,
          ...action.payload,
        },
      };

      // Update local storage with the new state
      localStorage.setItem("otp", JSON.stringify(newOtpReceived.OTP));
      return newOtpReceived;

    default:
      return state;
  }
};

export default registrationReducer;
