import React, { useState } from "react";
import OtpInput from "react-otp-input";

import image from "../../assets/undraw_confirmed_81ex.jpg";

import "./styles.scss";

const Otp = () => {
  const phoneNumber = +918465035940;
  const onResend = () => {};

  const [otp, setOtp] = useState("");

  return (
    <div className="otp-container">
      <img src={`${image}`} alt="logo" className="logo" />
      <h1>Please verify Mobile number</h1>
      <div className="number-edit-container">
        <p>
          Please sign in to your account<span>{phoneNumber}</span>
        </p>
        <p>Change Phone Number</p>
      </div>
      <div className="otp-input-container">
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={4}
          renderSeparator={<span className="separator"> </span>}
          renderInput={(props) => <input {...props} />}
          className="otpInput"
        />
      </div>
      <p>
        Didnâ€™t receive the code?<span onClick={onResend}>Resend</span>
      </p>
      <button className="submit-button">Verify</button>
    </div>
  );
};

export default Otp;
