import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import OtpInput from "react-otp-input";
import firebase from "../firebase";

import image from "../../assets/undraw_confirmed_81ex.jpg";

import "./styles.scss";

const Otp = () => {
  const navigate = useNavigate();
  const phoneNumber = +918465035940;
  const onResend = () => {};

  const [otp, setOtp] = useState("");
  const [error, setError] = useState("");

  const onChangeNumber = () => {
    navigate("/");
  };

  const onVerifyNumber = () => {
    onSubmitOTP();
    navigate("/success");
  };

  const onSubmitOTP = (e) => {
    e.preventDefault();
    const code = this.state.otp;
    console.log(code);
    window.confirmationResult
      .confirm(code)
      .then((result) => {
        // User signed in successfully.
        const user = result.user;
        console.log(JSON.stringify(user));
        alert("User is verified");
        // ...
      })
      .catch((error) => {
        setError(error);
      });
  };

  return (
    <div className="otp-container">
      <img src={`${image}`} alt="logo" className="logo" />
      <h1>Please verify Mobile number</h1>
      <div className="number-edit-container">
        <p>
          Please sign in to your account<span>{phoneNumber}</span>
        </p>
        <p onClick={onChangeNumber}>Change Phone Number</p>
      </div>
      <div className="otp-input-container">
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={4}
          renderSeparator={<span style={{ width: "8px" }}></span>}
          renderInput={(props) => <input {...props} />}
          inputStyle={{
            border: "solid #e5e5e5 1px",
            borderRadius: "8px",
            width: "48px",
            height: "48px",
            fontSize: "18px",
            color: "#333",
            fontWeight: "500",
            caretColor: "blue",
            fontStyle: "normal",
            fontFamily: "Work Sans",
            boxShadow: "6px 6px 5px 0px rgba(224,222,222,0.75)",
            outlineColor: "#80848c",
          }}
          focusStyle={{
            border: "1px solid #637290",
            outline: "none",
          }}
        />
      </div>
      <div>
        <p>
          Didnâ€™t receive the code?<span onClick={onResend}>Resend</span>
        </p>
        <button className="submit-button" onClick={onVerifyNumber}>
          Verify
        </button>
      </div>
    </div>
  );
};

export default Otp;
