import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import OtpInput from "react-otp-input";
import Snackbar from "../snackbar/snackbar";

import image from "../../assets/undraw_confirmed_81ex.jpg";

import "./styles.scss";

const SnackbarType = {
  success: "success",
  fail: "fail",
};

const Otp = () => {
  const navigate = useNavigate();
  const mobileNumber = localStorage.getItem("userRegisterdNumber");
  const snackbarRef = useRef(null);
  const otp = localStorage.getItem("OTP");

  const [enteredOTP, setEnteredOtp] = useState();
  const [validation, setValidation] = useState("");

  const onResend = () => {};

  useEffect(() => {
    setTimeout(() => {
      snackbarRef.current.show();
    }, 4000);
  });

  const onChangeNumber = () => {
    navigate("/");
  };

  const validateOTP = () => {
    const apiUrl = "http://localhost:3001/validate-otp";
    const data = {
      mobileNumber: `${mobileNumber}`,
      enteredOTP: `${enteredOTP}`,
    };

    const headers = {
      "Content-Type": "application/json",
    };

    axios
      .post(apiUrl, data, { headers: headers })
      .then((response) => {
        setValidation(response.data.message);
        console.log(response.data.message);
        navigate("/success");
      })
      .catch((error) => {
        console.error("Error:", error.message);
        setValidation(error.message);
      });
  };

  return (
    <div className="otp-container">
      <img src={`${image}`} alt="logo" className="logo" />
      <h1>Please verify Mobile number</h1>
      <div className="number-edit-container">
        <p>
          Please sign in to your account<span>{mobileNumber}</span>
        </p>
        <p onClick={onChangeNumber}>Change Phone Number</p>
      </div>
      <div className="otp-input-container">
        <OtpInput
          value={en}
          onChange={(value) => setEnteredOtp(value)}
          numInputs={4}
          renderSeparator={<span style={{ width: "8px" }}></span>}
          renderInput={(props) => <input {...props} />}
          inputStyle={{
            border: "solid #e5e5e5 1px",
            borderRadius: "8px",
            width: "48px",
            height: "48px",
            fontSize: "18px",
            color: "#333",
            fontWeight: "500",
            caretColor: "blue",
            fontStyle: "normal",
            fontFamily: "Work Sans",
            boxShadow: "6px 6px 5px 0px rgba(224,222,222,0.75)",
            outlineColor: "#80848c",
          }}
          focusStyle={{
            border: "1px solid #637290",
            outline: "none",
          }}
        />
        {validation && <p>{validation}</p>}
      </div>
      <div>
        <p>
          Didnâ€™t receive the code?<span onClick={onResend}>Resend</span>
        </p>
        <button className="submit-button" onClick={validateOTP}>
          Verify
        </button>
      </div>
      <Snackbar
        ref={snackbarRef}
        message={`Your OTP is ${otp}`}
        type={SnackbarType.success}
      />
    </div>
  );
};

export default Otp;
