const initialState = {
  userRegisteredNumber: {},
  OTP: {},
};

const resistrationReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_REGISTERED_NUMBER":
      // Update state
      const newRegisteredNumber = {
        ...state,
        userData: [...state.userRegisteredNumber, action.payload],
        userRegisteredNumber: action.payload,
      };

      // Update local storage with the new state
      localStorage.setItem(
        "userRegisteredNumber",
        JSON.stringify(newRegisteredNumber)
      );

      return newRegisteredNumber;

    case "ADD_OTP":
      // Update state
      const newOtpRecieved = {
        ...state,
        OTP: [...state.OTP, action.payload],
      };

      // Update local storage with the new state
      localStorage.setItem("otp", JSON.stringify(newOtpRecieved));
      return newOtpRecieved;

    default:
      return state;
  }
};

export default resistrationReducer;
