import { v4 as uuid } from "uuid";
import cookie from "js-cookie";

const initialState = {
  mobileNumber: 918465035940,
  userLogged: false, // Changed from userLoged to userLogged
};

const registrationReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_REGISTERED_NUMBER":
      // Update state for registered number
      return {
        ...state,
        ...action.payload, // Assuming action.payload contains the registered number
      };

    case "USER_LOGIN_STATUS":
      // Update state for user login status
      return {
        ...state,
        userLogged: action.payload, // Set userLogged directly from action payload
      };

    default:
      return state;
  }
};

export default registrationReducer;
