import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import OtpInput from "react-otp-input";
import Snackbar from "../snackbar/snackbar";

import image from "../../assets/undraw_confirmed_81ex.jpg";

import "./styles.scss";

const SnackbarType = {
  success: "success",
  fail: "fail",
};

const Otp = () => {
  const navigate = useNavigate();
  const mobileNumber = "+918465035940";
  const enteredOTP = 3223;
  const snackbarRef = useRef(null);

  const [otp, setOtp] = useState("");
  const [validation, setValidation] = useState("");

  const onResend = () => {};

  const onChangeNumber = () => {
    navigate("/");
  };
  const validateOTP = () => {
    axios
      .post("http://localhost:3000/validate-otp", { mobileNumber, enteredOTP })
      .then((response) => {
        setValidation(response.data.message);
        snackbarRef.current.show();
      })
      .catch((error) => {
        console.error("Error:", error.message);
        setValidation("OTP verification failed.");
      });
  };

  return (
    <Snackbar
        ref={snackbarRef}
        message="Task Completed Successfully!"
        type={SnackbarType.success}
      />
    
  );
};

export default Otp;
