import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import Snackbar from "../snackbar/snackbar";
import "./signin.scss";
import logo from "../../assets/AK_logo.jpg";

const SnackbarType = {
  success: "success",
  fail: "fail",
};

const Signin = () => {
  const navigate = useNavigate();
  const [mobileNumber, setMobileNumber] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const snackbarRef = useRef(null);
  const [loading, setLoading] = useState(false);

  const isPhoneNumberValid = (mobileNumber) => {
    // Use a more comprehensive phone number validation library if needed
    return /^\+[0-9]{10,15}$/.test(mobileNumber);
  };

  const handlePhoneChange = (value) => {
    setMobileNumber(value);
    localStorage.setItem("userRegisteredNumber", JSON.stringify(value));
    setPhoneError("");
  };

  const handleSubmit = async () => {
    if (!isPhoneNumberValid(mobileNumber)) {
      setPhoneError("Invalid phone number");
      return;
    }

    setPhoneError("");
    setLoading(true);

    try {
      const response = await generateOTP();
      snackbarRef.current.show("OTP Sent Successfully!", SnackbarType.success);
      navigate("/otp");
    } catch (error) {
      const errorMessage = error.response?.data?.error || "An error occurred.";
      console.error("Error:", error.message);
      snackbarRef.current.show(errorMessage, SnackbarType.fail);
    } finally {
      setLoading(false);
    }
  };

  const generateOTP = async () => {
    const apiUrl =
      process.env.REACT_APP_API_URL || "http://localhost:3001/generate-otp";
    const data = {
      mobileNumber: mobileNumber,
    };

    const response = await axios.post(apiUrl, data);
    console.log("OTP:", response.data.otp);
    return response;
  };

  return (
    <div className="signin-container">
      <img src={logo} alt="logo" className="logo" />
      <div className="welcome-text">
        <h1>Welcome Back</h1>
        <p>Please sign in to your account</p>
      </div>
      <div>
        <form className="number-input-container" onSubmit={handleSubmit}>
          <PhoneInput
            inputProps={{
              name: "contact",
              required: true,
              autoFocus: true,
            }}
            className="input"
            country={"in"}
            value={mobileNumber}
            onChange={(value) => handlePhoneChange(value)}
            inputStyle={{
              border: "none",
              borderRadius: "8px",
              width: "330px",
              height: "32px",
              fontSize: "18px",
              color: "#333",
              fontWeight: "500",
              caretColor: "blue",
              fontStyle: "normal",
              fontFamily: "Work Sans",
            }}
            containerStyle={{
              border: "none",
              backgroundColor: "none",
            }}
          />
          {phoneError ? (
            <p className="error-message">{phoneError}</p>
          ) : (
            <p>We will send you a one-time SMS message. Charges may apply.</p>
          )}
          <button className="submit-button" type="submit" disabled={loading}>
            {loading ? "Sending OTP..." : "Sign In with OTP"}
          </button>
        </form>
      </div>
      <Snackbar ref={snackbarRef} message="" type={SnackbarType.success} />
    </div>
  );
};

export default Signin;
